import axios from "axios"
export const getAllPizzas=()=>async dispatch=>{

dispatch({type:'GET_PIZZAZ_REQUEST'})
try{
const response =await axios.get('/api/pizzas/getpizzas')
console.log(response)
dispatch({type:'GET_PIZZAZ_SUCCESS' , payload : response.data})

}catch(error){
    dispatch({type:'GET_PIZZAZ_FAILED', payload : error})

}

}
